// This code is to demonstrate how nested for loop works.

public class Trial2 {

	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i =1; i<10; i++) {
			for(int j=1; j<10; j++) {
				System.out.print("*");
				}
			System.out.print("\n");
			for(int k=1; k<10; k++) {
				System.out.print("o");
				}
			System.out.println();
				}

}
}